                const ni = i + di;
                const nj = j + dj;

                if (ni >= 0 && ni < rows && nj >= 0 && nj < cols && grid[ni][nj] === 1) {
                    liveNeighbors++;
                }
            }

            // Apply rules
            if (grid[i][j] === 1) {
                // Live cell
                if (liveNeighbors < 2 || liveNeighbors > 3) {
                    newGrid[i][j] = 0;
                }
            } else {
                // Dead cell
                if (liveNeighbors === 3) {
                    newGrid[i][j] = 1;
                }
            }
        }
    }

    return newGrid;
