                // Live cell rules
                if (liveNeighbors < 2 || liveNeighbors > 3) {
                    newGrid[i][j] = 0;
                }
            } else {
                // Dead cell rules
                if (liveNeighbors === 3) {
                    newGrid[i][j] = 1;
                }
            }
        }
    }

